using System;using System.ComponentModel;using System.Collections.ObjectModel;using System.Data;using System.Data.SqlClient;using MessageBox = System.Windows.MessageBox;namespace Pubs.Model{    public class AuthorsFilter : INotifyPropertyChanged    {    #region Instance Properties        private string au_id;        public string Au_id        {             get             {              return au_id;             }             set             {
                au_id = value;              OnPropertyChanged("Au_id");             }        }         private string au_name;        public string Au_name        {         get         {          return au_name;         }         set         {
          au_name = value;          OnPropertyChanged("au_name");         }        }         #endregion Instance Properties        #region INotifyPropertyChanged Members        public event PropertyChangedEventHandler PropertyChanged;        private void OnPropertyChanged(string propertyName)        {         if (PropertyChanged != null)         {          PropertyChanged(this, new PropertyChangedEventArgs(propertyName));         }        }        #endregion INotifyPropertyChanged Members    }}