using System;using System.ComponentModel;using System.Collections.ObjectModel;using System.Data;using System.Data.SqlClient;using MessageBox = System.Windows.MessageBox;namespace Pubs.Model{	public class Sales : INotifyPropertyChanged	{		#region Instance Properties		private string stor_id;		public string Stor_id		{			get			{				return stor_id;			}			set			{				stor_id = value;				OnPropertyChanged("Stor_id");			}		}		private string ord_num;		public string Ord_num		{			get			{				return ord_num;			}			set			{				ord_num = value;				OnPropertyChanged("Ord_num");			}		}		private DateTime ord_date;		public DateTime Ord_date		{			get			{				return ord_date;			}			set			{				ord_date = value;				OnPropertyChanged("Ord_date");			}		}		private Int16 qty;		public Int16 Qty		{			get			{				return qty;			}			set			{				qty = value;				OnPropertyChanged("Qty");			}		}		private string payterms;		public string Payterms		{			get			{				return payterms;			}			set			{				payterms = value;				OnPropertyChanged("Payterms");			}		}		private string title_id;		public string Title_id		{			get			{				return title_id;			}			set			{				title_id = value;				OnPropertyChanged("Title_id");			}		}		#endregion Instance Properties		#region INotifyPropertyChanged Members		public event PropertyChangedEventHandler PropertyChanged;		private void OnPropertyChanged(string propertyName)		{			if (PropertyChanged != null)			{				PropertyChanged(this, new PropertyChangedEventArgs(propertyName));			}		}		#endregion INotifyPropertyChanged Members	}}