using System;using System.ComponentModel;using System.Collections.ObjectModel;using System.Data;using System.Data.SqlClient;using MessageBox = System.Windows.MessageBox;namespace Pubs.Model{    public class TitlesFilter : INotifyPropertyChanged    {    #region Instance Properties        private string type;        public string Type        {         get         {          return type;         }         set         {          type = value;          OnPropertyChanged("Type");         }        }         private AuthorsFilter au;        public AuthorsFilter Au        {         get         {          return au;         }         set         {
          au = value;          OnPropertyChanged("Au");         }        }         #endregion Instance Properties        #region INotifyPropertyChanged Members        public event PropertyChangedEventHandler PropertyChanged;        private void OnPropertyChanged(string propertyName)        {         if (PropertyChanged != null)         {          PropertyChanged(this, new PropertyChangedEventArgs(propertyName));         }        }        #endregion INotifyPropertyChanged Members    }}